import {
    DELETE_POST, SET_POSTS, LOADING_DATA, SET_GOAL, SET_DAILY_GOAL
  } from '../types';
  import axios from 'axios';
  

  export const deletePost = (postId) => (dispatch) => {
    axios
      .delete(`https://cors-anywere.herokuapp.com/https://us-central1-socialapp-35d2e.cloudfunctions.net/api/post/${postId}`)
      .then(() => {

        dispatch({ type: DELETE_POST, payload: postId });
      })
  };
 
  export const setDailyGoal = () => (dispatch) => {
    axios
      .get(`https://cors-anywere.herokuapp.com/https://us-central1-socialapp-35d2e.cloudfunctions.net/api/setgoal`)
      .then(() => {
        dispatch({ type:SET_DAILY_GOAL})
        console.log("siii")
      })
      .catch((err) => console.log(err,"yep"));
  }


  export const getPosts = () => (dispatch) => {
    dispatch({ type: LOADING_DATA });
    axios
       .get('https://cors-anywere.herokuapp.com/https://us-central1-socialapp-35d2e.cloudfunctions.net/api/posts')
       .then((res) => {
        dispatch({
          type: SET_POSTS,
          payload: res.data
        });
      })
      .catch((err) => {
        dispatch({
          type: SET_POSTS,
          payload: []
        });
      });
  };

  export const getDailyGoal = () => (dispatch) => {
    dispatch({ type: LOADING_DATA });
    axios
       .get('https://cors-anywere.herokuapp.com/https://us-central1-socialapp-35d2e.cloudfunctions.net/api/dailygoal')
       .then((res) => {
        dispatch({
          type: SET_GOAL,
          payload: res.data
        });
      })
      .catch((err) => {
        dispatch({
          type: SET_GOAL,
          payload: []
        });
      });
  };
