import {
    DELETE_POST,
  } from '../types';
  import axios from 'axios';
  

  export const deletePost = (postId) => (dispatch) => {
    axios
      .delete(`https://cors-anywere.herokuapp.com/https://us-central1-socialapp-35d2e.cloudfunctions.net/api/post/${postId}`)
      .then(() => {
        console.log("worked")
        dispatch({ type: DELETE_POST, payload: postId });
      })
      .catch((err) => console.log(err,"yep"));
  };
