import {
    DELETE_POST,
    SET_POSTS
  } from '../types';
  
  const initialState = {
    posts: [],
    post: {},
    loading: false
  };
  
  export default function(state = initialState, action) {
    switch (action.type) {
      case DELETE_POST:
        let index = state.Posts.findIndex(
          (post) => post.postId === action.payload
        );
        state.Posts.splice(index, 1);
        return {
          ...state
        };
        case SET_POSTS:
            return {
              ...state,
              posts: action.payload,
              loading: false
            };
      default:
        return state;
    }
  }
