import {
    DELETE_POST
  } from '../types';
  
  const initialState = {
    Posts: [],
    post: {},
    loading: false
  };
  
  export default function(state = initialState, action) {
    switch (action.type) {
      case DELETE_POST:
        let index = state.Posts.findIndex(
          (post) => post.postId === action.payload
        );
        state.Posts.splice(index, 1);
        return {
          ...state
        };
      default:
        return state;
    }
  }
