import React, { Component } from 'react';
import Grid from '@material-ui/core/Grid';
import Post from '../components/Post';
import { connect } from 'react-redux';
import { getPosts , getDailyGoal} from '../redux/actions/dataActions';
import PropTypes from 'prop-types';


class home extends Component {
    
    componentDidMount(){
        this.props.getPosts();
        this.props.getDailyGoal();
} 
    render() {
        console.log(this.props.data,"data")
        var recentPosts;
        const { posts, loading } = this.props.data;
        
        console.log(this.props.data.dailygoal)

        if(posts != null){
            recentPosts = posts.map((post) => <Post key={post.postId} post={post}/>);
        }

        return (
            <div>
                <Grid container>
                    <Grid item md={8}>
                        {recentPosts}
                    </Grid>
                </Grid>

            </div>
        )
    }
}

home.propTypes = {
    getPosts: PropTypes.func.isRequired,
    getDailyGoal: PropTypes.func.isRequired,
    data: PropTypes.object.isRequired
  };
  
  const mapStateToProps = (state) => ({
    data: state.data
  });
  
  export default connect(
    mapStateToProps,
    { getPosts ,getDailyGoal }
  )(home);
