import React, { Component } from 'react';
import withStyles from '@material-ui/core/styles/withStyles';
import PropTypes from 'prop-types';
import Grid from '@material-ui/core/Grid';
import Logo from '../imgs/loginimg.png';
import Typography from '@material-ui/core/Typography';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import axios from 'axios';


const styles = {

    form: {
        textAlign: 'center'
    },
    image: {

        margin:'60px auto 0px auto'
    },
    
    button: {
        margin:20
    }

}



class login extends Component {
    
    handleSubmit = (event) => {

        event.preventDefault();
        this.setState({loading:true});

        const userData = {
            email: this.state.email,
            password: this.state.password
        }
        

        axios.post('https://cors-anywhere.herokuapp.com/https://us-central1-socialapp-35d2e.cloudfunctions.net/api/login', userData)
        .then(res => {
            console.log(res.data);
            this.setState({
                loading:false
            });
            this.props.history.push('/');
        })
        .catch(err => {
            this.setState({
                errors: err,
                loading:false
            })
        })

    }
    handleChange = (event) => {
        console.log("yepp");
        this.setState({
            [event.target.name]: event.target.value
            
        
        });
    }

    constructor(props) {
        super();
    
        this.state = {
             email: '',
             password: '',
             loading: false,
             errors: {}
        }
    }
    

    render() {
        const { classes } = this.props;
        return (
            <Grid container className={classes.form}>
                <Grid item sm/>

                <Grid item sm>
                    <img src = {Logo} width = "150" className = {classes.image}></img>
                    <Typography variant = "h3">Login</Typography>
                    <form noValidate onSubmit={this.handleSubmit}>
                        <TextField onChange ={this.handleChange} id ="email" name="email" type="email" label="Email" className={classes.textfield} value = {this.state.email} fullWidth>
 
                        </TextField>
                        <TextField onChange ={this.handleChange} id ="password" name="password" type="password" label="Password" className={classes.textfield} value = {this.state.password} fullWidth>
 
                        </TextField>
                        <Button type = "submit" variant = "contained" color = "primary" className={classes.button}>Login</Button>
                    </form>
                </Grid>

                <Grid item sm/>
            </Grid>
        )
    }
}


export default withStyles(styles)(login);
