import { SET_USER, SET_ERRORS, CLEAR_ERRORS, LOADING_UI, SET_UNAUTHENTICATED, LOADING_USER } from '../types';
import axios from 'axios';


export const loginUser = (userData, history) => (dispatch) => {

    dispatch({type: LOADING_UI});

    axios
    .post('https://cors-anywhere.herokuapp.com/https://us-central1-socialapp-35d2e.cloudfunctions.net/api/login', userData)
    .then(res => {
        const FBIdToken = `Bearer ${res.data.token}`;
        localStorage.setItem('FBIdToken', `Bearer ${res.data.token}`);
        axios.defaults.headers.common['Authorization'] = FBIdToken;
        dispatch(getUserData());
        dispatch({type: CLEAR_ERRORS});
        history.push('/');
    })
    .catch(err => {
        dispatch({
            type: SET_ERRORS,
            payload: err.response.data
        })
    });
 }

 export const signupUser = (newUserData, history) => (dispatch) => {

    dispatch({type: LOADING_UI});

    axios
    .post('https://cors-anywhere.herokuapp.com/https://us-central1-socialapp-35d2e.cloudfunctions.net/api/signup', newUserData)
    .then(res => {
        const FBIdToken = `Bearer ${res.data.token}`;
        localStorage.setItem('FBIdToken', `Bearer ${res.data.token}`);
        axios.defaults.headers.common['Authorization'] = FBIdToken;
        dispatch(getUserData());
        dispatch({type: CLEAR_ERRORS});
        history.push('/');
    })
    .catch(err => {
        dispatch({
            type: SET_ERRORS,
            payload: err.response.data
        })
    });
 }
export const logoutUser = () => (dispatch)  => {
    localStorage.removeItem('FBIdToken');
    delete axios.defaults.headers.common['Authorization'];
    dispatch({type: SET_UNAUTHENTICATED});
}


 export const getUserData = () => (dispatch) => {
     
    dispatch({ type: LOADING_USER });
    axios
      .get('https://us-central1-socialapp-35d2e.cloudfunctions.net/api/user')
      .then((res) => {
        console.log("Fetinch User Data")
        dispatch({
          type: SET_USER,
          payload: res.data
        });
      })
      .catch((err) => console.log(err));
  };
