import React, { Component } from 'react';
import withStyles from '@material-ui/core/styles/withStyles';
import PropTypes from 'prop-types';
import Grid from '@material-ui/core/Grid';
import Logo from '../imgs/loginimg.png';
import Typography from '@material-ui/core/Typography';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import axios from 'axios';
import Link from '@material-ui/core/Link';
import CircularProgress from '@material-ui/core/CircularProgress';


const styles = {

    form: {
        textAlign: 'center'
    },
    image: {

        margin:'60px auto 0px auto'
    },
    
    button: {
        margin:20,
        position: 'relative'
    },

    progress: {
        position: 'absolute'
    }
    

}



class signup extends Component {
    
    handleSubmit = (event) => {

        event.preventDefault();
        this.setState({loading:true});

        const newUserData = {
            email: this.state.email,
            password: this.state.password,
            confirmPassword: this.state.confirmPassword,
            handle: this.state.username
        }
        

        axios.post('https://cors-anywhere.herokuapp.com/https://us-central1-socialapp-35d2e.cloudfunctions.net/api/signup', newUserData)
        .then(res => {
            console.log(res.data);
            localStorage.setItem('FBIdToken', `Bearer ${res.data.token}`);
            this.setState({
                loading:false
            });
            console.logs(this.props,"inside signup call");
            this.props.history.push('/');
        })
        .catch(err => {
            this.setState({
                errors: err,
                loading:false
            })
        })

    }
    handleChange = (event) => {
        console.log("yepp");
        this.setState({
            [event.target.name]: event.target.value
            
        
        });
    }

    constructor(props) {
        super();
    
        this.state = {
             email: '',
             password: '',
             confirmPassword: '',
             handle: '',
             loading: false,
             errors: {}
        }
    }
    

    render() {
        const { classes } = this.props;
        const {errors, loading} = this.state;

        return (
            <Grid container className={classes.form}>
                <Grid item sm/>

                <Grid item sm>
                    <img src = {Logo} width = "150" className = {classes.image}></img>
                    <Typography variant = "h3">SignUp</Typography>
                    <form noValidate onSubmit={this.handleSubmit}>

                        <TextField onChange ={this.handleChange}
                        id ="handle"
                        name="handle" 
                        type="text" 
                        label="Username" 
                        className={classes.textfield}
                        value = {this.state.handle} fullWidth>
                        </TextField>

                        <TextField onChange ={this.handleChange}
                        id ="email"
                        name="email" 
                        type="email" 
                        label="Email" 
                        className={classes.textfield}
                        value = {this.state.email} fullWidth>
                        </TextField>

                        <TextField onChange ={this.handleChange} 
                        id ="password" 
                        name="password"
                        type="password" 
                        label="Password" 
                        className={classes.textfield} value = {this.state.password} fullWidth>
                        </TextField>

                        <TextField onChange ={this.handleChange} 
                        id ="confirmPassword" 
                        name="confirmPassword"
                        type="password" 
                        label="Confirm Password" 
                        className={classes.textfield} value = {this.state.confirmPassword} fullWidth>
                        </TextField>

                        <Button type ="submit"
                         variant = "contained" 
                         color = "primary" 
                         className={classes.button}
                         disabled= {loading}>
                         Signup
                         {loading && (
                            <CircularProgress size = {25} className={classes.progress}/>     
                         )}
                         </Button>
                        <div>
                            Have an Account? <Link href="./login">Login</Link>
                        </div>
                    </form>
                </Grid>

                <Grid item sm/>
            </Grid>
        )
    }
}


export default withStyles(styles)(signup);
