import {
    DELETE_POST,
    SET_POSTS,
    LOADING_DATA,
    SET_GOAL,
    SET_DAILY_GOAL
  } from '../types';
  
  const initialState = {
    dailygoal: {},
    posts: [],
    post: {},
    loading: false
  };
  
  export default function(state = initialState, action) {
    switch (action.type) {
        case DELETE_POST:
        let index = state.Posts.findIndex(
          (post) => post.postId === action.payload
        );
        state.Posts.splice(index, 1);

        return {
          ...state
        };
        case SET_POSTS:
            return {
              ...state,
              posts: action.payload,
              loading: false
            };
        case SET_DAILY_GOAL:
          return {
            ...state
          };
        case SET_GOAL:
          return {
            ...state,
            dailygoal: action.payload,
            loading: false
          };
        case LOADING_DATA:
            return {
                ...state,
                loading:true
            }
      default:
        return state;
    }
  }
