import React, { Component } from 'react';
import Paper from '@material-ui/core/Paper';
import crown from '../imgs/crown.png';
import Typography from '@material-ui/core/Typography';
import Grid from '@material-ui/core/Grid';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemText from '@material-ui/core/ListItemText';
import dayjs from 'dayjs';
import relativeTime from 'dayjs/plugin/relativeTime';
import Card from '@material-ui/core/Card';
import { Divider } from '@material-ui/core';

dayjs.extend(relativeTime);


const styles = {
    paper : {
        margin:5,
        padding:5
    },
    img : {
        width: 50
    },
    header:{
        padding:5,
        marginTop:20,
        marginRight:"auto",
        marginLeft:9,
        marginBottom:10,
        backgroundColor: '#112647',
        marginBottom: '10px'
    },
    text1:{ 
        color: 'red'
    }
}



class Achievments extends Component {
    
    
    render() {

        var achievments,listofachievments;

        if(this.props.user.userData){
            achievments = this.props.user.userData.achievments;
            console.log(achievments, "from achievments")
            listofachievments = achievments.map((achievment) =>       
            
            <ListItem>
                <ListItemText primary= {achievment.goal} secondary={dayjs(achievment.accomplishedAt.toString()).fromNow()} />
                <hr></hr>
            </ListItem>
            );
        }


        return (
            <div>
                <Paper style={styles.paper}>
                    <Grid container>
                            <Grid item md={12} alignItems='center'>
                                <Grid item md={2}>
                                    <img src={crown} alt="crown" style={styles.img}/>
                                </Grid>
                                <Grid item md={8} alignContent='center'>
                                    <Typography style={styles.text1} variant="h4">Accomplished</Typography>
                                </Grid>
                            </Grid>

                        <Grid item md={12}>
                            <List>
                                {listofachievments}
                            </List>
                        </Grid>
                        <hr></hr>
                    </Grid>
                </Paper>
            </div>
        )
    }
}

Achievments.propTypes = {
    user: PropTypes.object.isRequired
  };
  
  
  const mapStateToProps = (state) => ({
    user: state.user
  });
  
  const mapActionsToProps = { };
  
  export default connect(mapStateToProps,mapActionsToProps)(Achievments);
