import React, { Component } from 'react';
import axios from 'axios';
import withStyles  from '@material-ui/core/styles/withStyles';
import dayjs from 'dayjs';
import relativeTime from 'dayjs/plugin/relativeTime';
import PropTypes from 'prop-types';

import Card from '@material-ui/core/Card';
import CardActionArea from '@material-ui/core/CardActionArea';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Typography from '@material-ui/core/Typography';
import {connect} from 'react-redux';
import Grid from '@material-ui/core/Grid';
import DeleteIcon from '@material-ui/icons/Delete';

const styles = {
    Card:{
        padding: 20,
        marginBottom:20,
    }
}

class Post extends Component {


    render() {

        dayjs.extend(relativeTime);
        var posts;
        var userHandle, body, createdAt, team;
        userHandle = this.props.post.userHandle;
        body = this.props.post.body;
        createdAt = this.props.post.createdAt.toString();
        team = this.props.post.team;

        const {user :{userData}} = this.props;

        var handle, authenticated;

        if(userData)  handle = userData.credentials.handle;
        console.log(handle,"from post.js");
        
        const deleteButton =
      authenticated && userHandle === handle ? (
        <h1>yes</h1>
      ) : null;

        
        
        return (
            <Card className = "Card">
                <Grid container>
                    <Grid item md={10}>
                        <Typography variant="bod1" color="primary">{userHandle}</Typography>
                        <Typography variant="body2">{body}</Typography>
                        <Typography variant="body2" color ="textSecondary">{dayjs(createdAt).fromNow()}</Typography>
                        <Typography variant="body2" color ="textSecondary">{team}</Typography>
                    </Grid>
                    <Grid item md={2} style={{marginLeft: "auto"}}>
                        <DeleteIcon></DeleteIcon>
                    </Grid>
                </Grid>
            </Card>
        
        )
    }
}
Post.propTypes = {
    user: PropTypes.object.isRequired,
    classes: PropTypes.object.isRequired
  };
  
  const mapStateToProps = (state) => ({
    user: state.user
  });
  
  export default connect(mapStateToProps)(withStyles(styles)(Post));
