import React, { Component } from 'react';
import Paper from '@material-ui/core/Paper';
import crown from '../imgs/crown.png';
import Typography from '@material-ui/core/Typography';
import Grid from '@material-ui/core/Grid';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemText from '@material-ui/core/ListItemText';
import dayjs from 'dayjs';
import relativeTime from 'dayjs/plugin/relativeTime';
dayjs.extend(relativeTime);
import { Divider } from '@material-ui/core';

const styles = {
    paper : {
        margin:5,
        padding:10
    },
    img : {
        width: 50
    }
}



class Achievments extends Component {
    
    
    render() {

        var achievments,listofachievments;

        if(this.props.user.userData){
            achievments = this.props.user.userData.achievments;
            console.log(achievments, "from achievments")
            listofachievments = achievments.map((achievment) =>       
            
            <ListItem>
                <ListItemText primary= {achievment.goal} secondary={dayjs(achievment.accomplishedAt.toString()).fromNow()} />
                <Divider></Divider>
            </ListItem>
            );
        }


        return (
            <div>
                <Paper style={styles.paper}>
                    <Grid container style={{margin: "auto"}}>
                        <Grid item md={2}>
                            <img src={crown} alt="crown" style={styles.img}/>
                        </Grid>
                        <Grid item md={10}>
                            <Typography variant="h4">Accomplished</Typography>
                            <List>
                                {listofachievments}
                            </List>
                        </Grid>
                        <hr></hr>
                    </Grid>
                </Paper>
            </div>
        )
    }
}

Achievments.propTypes = {
    user: PropTypes.object.isRequired
  };
  
  
  const mapStateToProps = (state) => ({
    user: state.user
  });
  
  const mapActionsToProps = { };
  
  export default connect(mapStateToProps,mapActionsToProps)(Achievments);
